select * from (select s.id, s.supplier, s.contacts, s.telephone, s.phone_num, s.email, (case when s.type='供应商' then ifnull(s.begin_need_pay,0) else ifnull(s.begin_need_get,0) end) begin_need, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preDebtMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preReturnDebtMoney, (select abs(ifnull(sum(ai.each_amount),0)) from jsh_account_item ai left join jsh_depot_head dh on ai.bill_id=dh.id and ifnull(dh.delete_flag,'0') !='1' where dh.organ_id=s.id and dh.oper_time <= #{beginTime} and ifnull(ai.delete_flag,'0') !='1') preBackMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') debtMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') returnDebtMoney, (select abs(ifnull(sum(ai.each_amount),0)) from jsh_account_item ai left join jsh_depot_head dh on ai.bill_id=dh.id and ifnull(dh.delete_flag,'0') !='1' where dh.organ_id=s.id and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(ai.delete_flag,'0') !='1') backMoney from jsh_supplier s where s.enabled=1 and s.type=#{supplierType}
and s.id = #{organId}
and s.id in (
#{organId}
)
and ifnull(s.delete_flag,'0') !='1') tb where begin_need+preDebtMoney-preBackMoney+debtMoney-backMoney>0 order by begin_need+preDebtMoney-preReturnDebtMoney-preBackMoney+debtMoney-returnDebtMoney-backMoney desc
limit #{offset},#{rows}

select count(1) from (select s.id, s.supplier, s.contacts, s.telephone, s.phone_num, s.email, (case when s.type='供应商' then ifnull(s.begin_need_pay,0) else ifnull(s.begin_need_get,0) end) begin_need, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preDebtMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preReturnDebtMoney, (select abs(ifnull(sum(ai.each_amount),0)) from jsh_account_item ai left join jsh_depot_head dh on ai.bill_id=dh.id and ifnull(dh.delete_flag,'0') !='1' where dh.organ_id=s.id and dh.oper_time <= #{beginTime} and ifnull(ai.delete_flag,'0') !='1') preBackMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') debtMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') returnDebtMoney, (select abs(ifnull(sum(ai.each_amount),0)) from jsh_account_item ai left join jsh_depot_head dh on ai.bill_id=dh.id and ifnull(dh.delete_flag,'0') !='1' where dh.organ_id=s.id and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(ai.delete_flag,'0') !='1') backMoney from jsh_supplier s where s.enabled=1 and s.type=#{supplierType}
and s.id = #{organId}
and s.id in (
#{organId}
)
and ifnull(s.delete_flag,'0') !='1') tb where begin_need+preDebtMoney-preReturnDebtMoney-preBackMoney+debtMoney-returnDebtMoney-backMoney>0

select sum(begin_need) begin_need, sum(preDebtMoney) preDebtMoney, sum(preReturnDebtMoney) preReturnDebtMoney, sum(preBackMoney) preBackMoney, sum(debtMoney) debtMoney, sum(returnDebtMoney) returnDebtMoney, sum(backMoney) backMoney from (select s.id, (case when s.type='供应商' then ifnull(s.begin_need_pay,0) else ifnull(s.begin_need_get,0) end) begin_need, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preDebtMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time <= #{beginTime} and ifnull(dh.delete_flag,'0') !='1') preReturnDebtMoney, (select abs(ifnull(sum(ai.each_amount),0)) from jsh_account_item ai left join jsh_depot_head dh on ai.bill_id=dh.id and ifnull(dh.delete_flag,'0') !='1' where dh.organ_id=s.id and dh.oper_time <= #{beginTime} and ifnull(ai.delete_flag,'0') !='1') preBackMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{type} and dh.sub_type=#{subType} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') debtMoney, (select ifnull(sum(dh.discount_last_money),0)+ifnull(sum(dh.other_money),0)-ifnull(sum(dh.deposit),0)-abs(ifnull(sum(dh.change_amount),0)) from jsh_depot_head dh where dh.organ_id=s.id and dh.type=#{typeBack} and dh.sub_type=#{subTypeBack} and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(dh.delete_flag,'0') !='1') returnDebtMoney, (select abs(ifnull(sum(ai.each_amount),0)) from jsh_account_item ai left join jsh_depot_head dh on ai.bill_id=dh.id and ifnull(dh.delete_flag,'0') !='1' where dh.organ_id=s.id and dh.oper_time>#{beginTime} and dh.oper_time <= #{endTime} and ifnull(ai.delete_flag,'0') !='1') backMoney from jsh_supplier s where s.enabled=1 and s.type=#{supplierType}
and s.id = #{organId}
and s.id in (
#{organId}
)
and ifnull(s.delete_flag,'0') !='1') tb where begin_need+preDebtMoney-preReturnDebtMoney-preBackMoney+debtMoney-returnDebtMoney-backMoney>0

select ifnull(sum(${modeName}),0) as allMoney from jsh_depot_head where type=#{type} and sub_type = #{subType}
and organ_id =#{supplierId} and oper_time <=#{endTime}
and ifnull(delete_flag,'0') !='1'

