and organ_id =#{supplierId} and oper_time <=#{endTime}
and ifnull(delete_flag,'0') !='1'

select dh.*, concat(dh.sub_type,dh.type) as depotHeadType, a.name AccountName, s.supplier OrganName, s.contacts, s.telephone, s.address from jsh_depot_head dh left join jsh_supplier s on dh.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1' left join jsh_account a on dh.account_id=a.id and ifnull(a.delete_Flag,'0') !='1' where 1=1
and dh.number=#{number}
and ifnull(dh.delete_flag,'0') !='1'

